import React from 'react';
import { connect } from 'react-redux';
import Card from '../Card/Card.jsx';
import './favorites.css';
import styled from "styled-components"

export const CardStyled = styled.div`
color: white;
border-top: 10px solid rgb(230,14,214);
border-right: 5px double rgb(230,14,214);
border-bottom: 10px solid rgb(230,14,214);
border-left: 5px double rgb(230,14,214);
background: black;
border-radius:18px;
width: fit-content;
padding: 0px;
box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.75);
display: flex;
flex-direction: column;
margin: 0 auto;
transition: 0.8s;
`;

const Favorites = ({ myFavorites }) => {
  return( 
    <CardStyled className="favorites-container">
      <h1>My Favorites</h1>
    
      <div>
            {myFavorites?.map(({id, name, species, gender, image})=>(
                <Card
                id={id}
                key={id}
                name={name}
                species={species}
                gender={gender}
                image={image}
                />
            ))}
        </div>
    </CardStyled>
  )
};

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};


export default connect(mapStateToProps, null)(Favorites);
