import { Link, useNavigate } from "react-router-dom";
import styled from "styled-components";
import { connect } from 'react-redux';
import { addFavorite, removeFavorite } from '../../redux/actions';
import React, { useState, useEffect } from "react";



export const CardStyled = styled.div`
color: white;
border-top: 10px solid rgb(230,14,214);
border-right: 5px double rgb(230,14,214);
border-bottom: 10px solid rgb(230,14,214);
border-left: 5px double rgb(230,14,214);
background: black;
border-radius:18px;
width: fit-content;
padding: 0px;
box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.75);
display: flex;
flex-direction: column;
margin: 0 auto;
transition: 0.8s;
`;

export const Boton = styled.button`
  border-radius: 5px;
  border: 3px;
  width: fit-content;
  height: fit-content;
  transition: 1.0s;
  :hover {
    background-color: red;
    box-shadow: -3px -3px 80px 6px rgba(0, 0, 0, 1);
  }
`;

function Card(props) {
  const navigate= useNavigate();
const [isFav, setIsFav] = useState(false);
  const { character, onClose, addFavorite, removeFavorite, myFavorites } = props;
  const [closeBtn, setCloseBtn] = useState(true)
  
  useEffect(() => {
    if(!onClose){
      setCloseBtn(false)
    }
  })

useEffect(() => {
    myFavorites.forEach((fav) => {
      if (fav.id === props.id) {
        setIsFav(true);
      }
    });
  }, [myFavorites, props.id]);

  const handleFavorite = () => {
    isFav ? removeFavorite(character.id) : addFavorite(props);
    setIsFav(!isFav)
  };




  return (
    <div>
      {closeBtn ?
        (<button 
          onClick={() => onClose(character.id)}>X</button>)
        : null}
      {
        isFav ? (<button onClick={handleFavorite}>‚ù§Ô∏è</button>)
          :
          (<button onClick={handleFavorite}>ü§ç</button>)}
      <h2  >
        {character.name}</h2>
      <div >
        <h2>{character.species}</h2>
        <h2>{character.gender}</h2>
      </div>
      <img src={character.image} alt="" onClick={() => navigateHandler()} />
    </div>
  );
}

export function mapDispatchToProps(dispatch) {
  return {
    addFavorite: function (character) { dispatch(addFavorite(character)) },
    removeFavorite: function (id) { dispatch(removeFavorite(id)) }
  };
};
export function mapStateToProps(state) {
  return {
    myFavorites: state.myFavorites
  }
};

export default connect(mapStateToProps, mapDispatchToProps)(Card);
