import { Link } from "react-router-dom";
import styled from "styled-components";
import { connect } from 'react-redux';
import { addFavorite, removeFavorite } from '../../redux/actions';
import React, { useState, useEffect } from "react";



export const CardStyled = styled.div`
color: white;
border-top: 10px solid rgb(230,14,214);
border-right: 5px double rgb(230,14,214);
border-bottom: 10px solid rgb(230,14,214);
border-left: 5px double rgb(230,14,214);
background: black;
border-radius:18px;
width: fit-content;
padding: 0px;
box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.75);
display: flex;
flex-direction: column;
margin: 0 auto;
transition: 0.8s;
`;

export const Boton = styled.button`
  border-radius: 5px;
  border: 3px;
  width: fit-content;
  height: fit-content;
  transition: 1.0s;
  :hover {
    background-color: red;
    box-shadow: -3px -3px 80px 6px rgba(0, 0, 0, 1);
  }
`;

 function Card (props) {

  const [isFavorite, setIsFavorite] = useState(false);;
   
  const handleFavorite = () => {
   if (isFavorite) {
     props.removeFavorite(props.id);
     setIsFavorite(false);
   } else {
     props.addFavorite(props.id);
     setIsFavorite(true);
   }
 };
 
 useEffect(() => {
  if (props.myFavorites) {
  props.myFavorites.forEach((fav) => {
  if (fav.id === props.id) {
  setIsFavorite(true);
  }
  });
  }
  }, [props.myFavorites, props.id]);

 return (
    <CardStyled>
      <Boton onClick={props.onClose}>X</Boton>
      <Link to={`/detail/${props.id}`}>
        <p>{props.name}</p>
      </Link>
      <p>{props.species}</p>
      <p>{props.gender}</p>
      <img src={props.image} alt="una imagen" />
      {
        isFavorite ? (
          <button onClick={handleFavorite}>‚ù§Ô∏è</button>
        ) : (
          <button onClick={handleFavorite}>ü§ç</button>
        )
      }
    </CardStyled>
  );
};
 const mapDispatchToProps = (dispatch) => {
   return {
    addFavorite: (character) => dispatch(addFavorite(character)),
    removeFavorite: (id) => dispatch(removeFavorite(id)),
   };
 };
 const mapStateToProps = state => ({
  myFavorites: state.myFavorites
});
 
 export default connect(mapStateToProps, mapDispatchToProps)(Card);
