import { Link } from "react-router-dom";
import styled from "styled-components";
import { connect } from 'react-redux';
import { addFavorite, removeFavorite } from '../../redux/actions';
import React, { useState, useEffect } from "react";


export const CardStyled = styled.div`
  color: white;
  border-top: 10px solid rgb(230,14,214);
  border-right: 5px double rgb(230,14,214);
  border-bottom: 10px solid rgb(230,14,214);
  border-left: 5px double rgb(230,14,214);
  background: black;
  border-radius:18px;
  width: fit-content;
  padding: 0px;
  box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.75);
  display: flex;
  flex-direction: column;
  margin: 0 auto;
  transition: 0.8s;
`;

export const Boton = styled.button`
  border-radius: 5px;
  border: 3px;
  width: fit-content;
  height: fit-content;
  transition: 1.0s;
  :hover {
    background-color: red;
    box-shadow: -3px -3px 80px 6px rgba(0, 0, 0, 1);
  }
`;

function Card(props) {
  const [isFav, setIsFav] = useState(false);

  useEffect(() => {
    const isFavorite = props.myFavorites.some(fav => fav.id === props.id);
    setIsFav(isFavorite);
  }, [props.myFavorites, props.id]);

  const handleFavorite = () => {
    if (isFav) {
      setIsFav(false);
      props.removeFavorite(props.id);

    } else {
      
      setIsFav(true);
      props.addFavorite(props.character);
    }
  };

  return (
    <CardStyled>
      <Boton onClick={props.onClose}>X</Boton>
      <Link to={`/detail/${props.id}`}>
        <CardStyled>{props.name}</CardStyled>
      </Link>
      <CardStyled>{props.species}</CardStyled>
      <CardStyled>{props.gender}</CardStyled>
      <img src={props.image} alt="una imagen" />
      {isFav ? (
        <button onClick={handleFavorite}>‚ù§Ô∏è</button>
      ) : (
        <button onClick={handleFavorite}>ü§ç</button>
      )}
    </CardStyled>
  );
}

export function mapDispatchToProps(dispatch) {
  return {
    addFavorite: function (character) {
      dispatch(addFavorite(character));
    },
    removeFavorite: function (id) {
      dispatch(removeFavorite(id));
    }
  };
}

export function mapStateToProps(state) {
  return {
    myFavorites: state.myFavorites
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Card);
